import Graph from 'react-vis-network-graph';
import React, { useState } from "react";
require('dotenv').config()

const API_URI = 'http://' + process.env.REACT_APP_API_HOST + ':' + process.env.REACT_APP_API_PORT


function RGV({nodes,edges}) {
  console.log('nodes:',nodes)

  const [state, setState] = useState({
    counter: 5,
    graph: {
      nodes: [
        { id: 1, label: "Node 1", color: "#e04141" },
        { id: 2, label: "Node 2", color: "#e09c41" },
        { id: 3, label: "Node 3", color: "#e0df41" },
        { id: 4, label: "Node 4", color: "#7be041" },
        { id: 5, label: "Node 5", color: "#41e0c9" }
      ],
      edges: [
        { id:12,from: 1, to: 2 ,label:'12'},
        { id:13,from: 1, to: 3 ,label:'13'},
        { id:24,from: 2, to: 4 ,label:'24'},
        { id:25,from: 2, to: 5,label:'25' }
      ]
    },
    events: {
      select: ({ nodes, edges }) => {
        console.log("Selected nodes:"+nodes);
        console.log("Selected edges:"+edges);
      },
      selectEdge:(e)=>{
        var {nodes,edges}=e;
        if (edges.length>=1){
          console.log('selectEdge:'+edges+'\nNodes:'+nodes);
        }
      }
    }
  })
  const { graph, events } = state;

  const options = {
    layout: {
      hierarchical: false
    },
    edges: {
      color: "#000000"
    },
    height: "256px"
  };

  return (
    <Graph 
    graph={graph} 
    options={options} 
    events={events} 
    style={{ height: "250px" }} 
    />
  );
}

export default RGV;
