import Graph from 'react-vis-network-graph';
import React, { useState,memo } from "react";
require('dotenv').config()

const API_URI = 'http://' + process.env.REACT_APP_API_HOST + ':' + process.env.REACT_APP_API_PORT


function RGV({data}) {


  const [state, setState] = useState({
    counter: 5,
    graph: {
      nodes: data.nodes,
      edges: data.edges
    },
    events: {
      select: ({ nodes, edges }) => {
        console.log("Selected nodes:"+nodes);
        console.log("Selected edges:"+edges);
      },
      selectEdge:(e)=>{
        var {nodes,edges}=e;
        if (edges.length>=1){
          console.log('selectEdge:'+edges+'\nNodes:'+nodes);
        }
      }
    }
  })

  const { graph, events } = state;

  const options = {
    layout: {
      hierarchical: false
    },
    edges: {
      color: "#000000"
    },
    height: "256px"
  };

  console.log('graph data:',data)
  let tmp_graph={nodes:data.nodes,edges:data.edges}
  setState((x)=>  {return {...x,graph:tmp_graph}});

  return (
    <Graph 
    graph={graph} 
    options={options} 
    events={events} 
    style={{ height: "250px" }} 
    />
  );
}

export default memo(RGV);
